$(document).ready(function () {
    var pathname = window.location.pathname;
    var o = $('ul.navbar-nav li a[href="' + pathname + '"]');
    $(o).each(function () {
        if ($(this).attr('href') == pathname) {
            var nav = o.closest("li");
            var navhref = o.closest("a");
            nav.addClass("active");
            navhref.addClass("active");
        }
    });

    var l = $('div.menuleft-preview a[href="' + pathname + '"]');
    $(l).each(function () {
        if ($(this).attr('href') == pathname) {
            var navCate = l.closest("ul");
            navCate.addClass("show");
            var nav = l.closest("a");
            nav.addClass("active");
        }        
    });
});